# Fetchcore SDK Python
from fetchcore.resources import TimestampedResource
from fetchcore.exceptions import ValidationError, UnsupportedOperation
from fetchcore.utils import Number


class DockGroupDraft(TimestampedResource):
    """
    Class for Fetchcore map dock groups.
    """

    # The endpoint in fetchcore for dock groups
    endpoint = 'maps/annotations/draft/groups/docks'

    read_only_fields = ['docks']

    def __init__(self, id=None, name=None, map=None, modifiers=None, dock_group=None, docks=None, created=None,
                 modified=None, **kwargs):
        """
        :param integer id: The ID of the dock group.
        :param string name: A human-readable name of the dock group.
        :param map: The map this dock group is associated with.
        :param modifiers: The users who have modified this annotation.
        :param dock_group: The dock group associated with this dock group draft.
        :param docks: The docks associated with this dock group.
        :param created: (string|datetime.datetime) The date and time of this dock group's creation.
        :param modified: (string|datetime.datetime) The date and time this dock group was last modified.
        """
        super(DockGroupDraft, self).__init__(id=id, created=created, modified=modified, **kwargs)

        self.name = name
        self.map = map
        self.modifiers = modifiers
        if dock_group:
            self.dock_group_id = dock_group
        if docks is None:
            self._set('docks', [])
        elif not isinstance(docks, list):
            raise ValidationError("Docks must be a list, not a %s." % type(docks).__name__)
        else:
            self._set('docks', docks)

    @property
    def docks(self):
        """Get the docks associated with this dock group

        :return: The docks
        """
        return self._get("docks")

    @property
    def name(self):
        """Gets the name of this dock group

        :return: The dock group name
        """
        return self._get("name")

    @name.setter
    def name(self, value):
        """Sets the name of this dock group

        :param value: The dock group name
        :raises Validation Error if value is not a string or is an empty string
        """
        if isinstance(value, basestring):
            if not value:
                raise ValidationError("Dock group name cannot be an empty string")
            self._set("name", value)
        else:
            raise ValidationError("Dock group name must be a string, not a %s" % type(value).__name__)

    @property
    def map_id(self):
        """Get the associated dock group map ID for this dock group.

        :return: The dock group map ID.
        """
        return self._get('map')

    @map_id.setter
    def map_id(self, map_id):
        """Set the associated dock group map ID for this dock group.

        :param integer map_id: The dock group map ID.
        :raise fetchcore.exceptions.ValidationError: Thrown if map_id not a finite positive integer.
        """
        if Number.is_integer(map_id):
            if not Number.is_finite_positive(map_id):
                raise ValidationError("Dock group map ID must be finite positive (item is %s)." % map_id)
            self._set('map', map_id)
        else:
            raise ValidationError("Dock group map ID must be an integer (%s is %s)."
                                  % (map_id, type(map_id).__name__))

    @property
    def map(self):
        """Get the associated dock group map for this dock group.

        :return: The Map object.
        """
        from fetchcore.resources.maps import Map
        return Map.load(self.map_id)

    @map.setter
    def map(self, map):
        """Set the associated dock group map for this dock group.

        :param map: (integer|Map) An dock group map or dock group map ID.
        :type map: int, Map
        :raise fetchcore.exceptions.ValidationError: Thrown if map is not an dock group map object or an integer or
            None.
        """
        from fetchcore.resources.maps import Map
        if isinstance(map, Map):
            if not map.is_set("id"):
                map.save()
            self.map_id = map.id
        elif isinstance(map, int):
            self.map_id = map
        else:
            raise ValidationError('Map can only be an integer or Map (%s is %s).' % (map, type(map).__name__))

    @property
    def modifier_ids(self):
        """Get the users who modified this annotation.

        :return: The list of user IDs.
        """
        return self._get('modifiers')

    @property
    def modifiers(self):
        """Get the user objects for the users who modified this annotation.

        :return: The list of modifiers as User objects.
        """
        from fetchcore.resources import User
        return [User.load(modifier_id) for modifier_id in self.modifier_ids]

    @modifiers.setter
    def modifiers(self, modifier_ids):
        """Set the the users who modified this annotation.

        :param modifier_ids: A list of user IDs.
        :type modifier_ids: list of integers
        :raise: ValidationError: Thrown if modifier_ids is not a list, if each item's ID
                is not an integer, or if each item's ID in the list is not a finite positive integer.
        """
        if modifier_ids is None:
            self._set('modifiers', [])
        elif not isinstance(modifier_ids, list):
            raise ValidationError("Modifier ids must be a list, not a %s." % type(modifier_ids).__name__)
        else:
            modifier_ids = list(set(modifier_ids))
            for modifier_id in modifier_ids:
                if not Number.is_integer(modifier_id):
                    raise ValidationError("Each item in modifier IDs must be an int (%s is %s)."
                                          % (modifier_id, type(modifier_id).__name__))
                elif not Number.is_finite_positive(modifier_id):
                    raise ValidationError(
                        "Each item in modifier IDs must be finite positive (item is %s)." % modifier_id)
            self._set('modifiers', modifier_ids)

    @property
    def dock_group_id(self):
        """Get the associated dock group ID for this dock group draft.

        :return: The dock group ID.
        """
        return self._get('dock_group')

    @dock_group_id.setter
    def dock_group_id(self, dock_group_id):
        """Set the associated dock group ID for this dock group draft.

        :param integer dock_group_id: The dock group ID.
        :raise fetchcore.exceptions.ValidationError: Thrown if dock_group_id not a finite positive integer.
        """
        if dock_group_id is None:
            self._set('dock_group', dock_group_id)
        elif Number.is_integer(dock_group_id):
            if not Number.is_finite_positive(dock_group_id):
                raise ValidationError("Dock group ID must be finite positive (item is %s)." % dock_group_id)
            self._set('dock_group', dock_group_id)
        else:
            raise ValidationError("Dock group ID must be an integer (%s is %s)."
                                  % (dock_group_id, type(dock_group_id).__name__))


class DockGroup(DockGroupDraft):
    """
    Class for Fetchcore map dock groups.
    """

    # The endpoint in fetchcore for dock groups
    endpoint = 'maps/annotations/groups/docks'

    read_only_fields = ['docks']

    def __init__(self, id=None, name=None, map=None, modifiers=None, docks=None, created=None, modified=None, **kwargs):
        """
        :param integer id: The ID of the dock group
        :param string name: A human-readable name of the dock group
        :param created: (string|datetime.datetime) The date and time of this dock group's creation.
        :param modified: (string|datetime.datetime) The date and time this dock group was last modified.
        """
        super(DockGroup, self).__init__(id=id, created=created, modified=modified, name=name, map=map, docks=docks,
                                        modifiers=modifiers, **kwargs)

    def save(self, _=None):
        """
        Overwrites base class dave. Live annotations are immutable and cannot be created on the server.

        :param _: Unused client parameter.
        :raise fetchcore.exceptions.UnsupportedOperation:
        """
        raise UnsupportedOperation("Annotations are immutable and cannot be saved directly to the server.")

    def update(self, _=None):
        """
        Overwrites base class update. Live annotations are immutable and cannot be updated on the server.

        :param _: Unused client parameter.
        :raise fetchcore.exceptions.UnsupportedOperation:
        """
        raise UnsupportedOperation("Annotations are immutable and cannot be updated on the server.")

    def delete(self, _=None):
        """
        Overwrites base class delete. Live annotations are immutable and cannot be deleted on the server.

        :param _: Unused client parameter.
        :raise fetchcore.exceptions.UnsupportedOperation:
        """
        raise UnsupportedOperation("Annotations are immutable and cannot be deleted on the server.")
