# Fetchcore SDK Python
from fetchcore.definitions import ActionPreemption
from fetchcore.resources import ActionTemplate
from fetchcore.resources.tasks.actions.definitions import BaseLocalize


class LocalizeTemplate(ActionTemplate, BaseLocalize):
    """
    The LOCALIZE action template provides an initial estimate of the robot's current position and orientation
    in the map.
    """
    required_fields = ["pose_estimate"]

    def __init__(
            self, id=None, action_definition="LOCALIZE", preemptable=ActionPreemption.NONE, pose_estimate=None,
            inputs=None, task_template=None, created=None, modified=None, on_complete=None, on_pause=None,
            on_resume=None, **kwargs
    ):
        super(LocalizeTemplate, self).__init__(
            id=id, action_definition=action_definition, preemptable=preemptable, pose_estimate=pose_estimate,
            inputs=inputs, created=created, modified=modified, task_template=task_template, on_complete=on_complete,
            on_pause=on_pause, on_resume=on_resume, **kwargs
        )
