# Fetchcore SDK Python
from fetchcore.definitions import ActionPreemption
from fetchcore.resources import ActionTemplate
from fetchcore.resources.tasks.actions.definitions import BaseSurvey


class SurveyTemplate(ActionTemplate, BaseSurvey):
    """
    The SURVEY action template allows the robot to perform data survey activities.
    """
    optional_fields = ["intermediate_task_template_id", "max_velocity", "max_angular_velocity", "limit_velocity"]

    required_fields = ["survey_path_id"]

    def __init__(
            self, id=None, action_definition="SURVEY", preemptable=ActionPreemption.NONE, survey_path_id=None,
            intermediate_task_template_id=None, max_velocity=None, max_angular_velocity=None, limit_velocity=None,
            inputs=None, task_template=None, created=None, modified=None, on_complete=None, on_pause=None,
            on_resume=None, **kwargs
    ):
        super(SurveyTemplate, self).__init__(
            id=id, action_definition=action_definition, preemptable=preemptable, survey_path_id=survey_path_id,
            intermediate_task_template_id=intermediate_task_template_id, max_velocity=max_velocity,
            max_angular_velocity=max_angular_velocity, limit_velocity=limit_velocity, inputs=inputs,
            task_template=task_template, created=created, modified=modified, on_complete=on_complete, on_pause=on_pause,
            on_resume=on_resume, **kwargs
        )
