# Fetchcore SDK Python
from fetchcore.definitions import ActionStatus, ActionPreemption
from fetchcore.resources import Action
from fetchcore.resources.tasks.actions.definitions import BaseURL


class URLAction(Action, BaseURL):
    """
    The URL action sends a new URL for the web browser on the HMI screen to display.
    """
    required_fields = ["url"]

    def __init__(
            self, id=None, action_definition="URL", preemptable=ActionPreemption.NONE, task=None,
            status=ActionStatus.NEW, start=None, end=None, url=None, inputs=None, outputs=None, states=None,
            on_complete=None, on_pause=None, on_resume=None, created=None, modified=None, **kwargs
    ):
        super(URLAction, self).__init__(
            id=id, action_definition=action_definition, preemptable=preemptable, task=task, status=status, start=start,
            end=end, url=url, inputs=inputs, outputs=outputs, states=states, on_complete=on_complete,
            on_pause=on_pause, on_resume=on_resume, created=created, modified=modified, **kwargs
        )
